<?php	include '../connect.php';	?>	<html><head><title>WebShop</title></head><body>	<?php			$newUsername = $_POST["username"];			$newPassword = $_POST["password"];		$newName = $_POST["name"];		$newEmail = $_POST["email"];		$newAddress = $_POST["address"];			$newNin = $_POST["nin"];			$newPhone = $_POST["phone"];		try {  			$sql = "INSERT INTO customers (name, address, phone, email, nin) 					VALUES (?, ?, ?, ?, ?)";
			$pdo->prepare($sql);			$pdo->execute([$newName, $newAddress, $newPhone, $newEmail, $newNin]);				$lastID = $pdo->lastInsertId();	
			$sql2 = "INSERT INTO users (role, username, password, user_id) 					   VALUES (1, ?, ?, '$lastID');";			$pdo->prepare($sql2);			$pdo->execute([$newUsername, $newPassword]);	 			echo 'User successfully registered!';    		}catch(PDOException $e)    {				echo 'User could not be registered! <br>';    			echo $sql . '<br>' . $e->getMessage();    		}	?>	</body></html>