<?php	include 'header.php';	include 'connect.php';	$newUsername = $_POST["username"];		$newPassword = password_hash($_POST["password"], PASSWORD_DEFAULT);	$newName = $_POST["name"];	$newEmail = $_POST["email"];	$newAddress = $_POST["address"];		$newNin = $_POST["nin"];		$newPhone = $_POST["phone"];		$noEmtpyFields = $newUsername && $newPassword && $newName && $newEmail &&					$newAddress && $newNin && $newPhone;		try{		if($noEmtpyFields) {						$stmt_cust = $pdo->prepare('INSERT INTO customers (name, address, phone, email, nin) 					VALUES (?,?,?,?,?)');			$stmt_cust->execute([$newName, $newAddress, $newPhone, $newEmail, $newNin]);			$lastID = $pdo->lastInsertId();						$stmt_usr = $pdo->prepare('INSERT INTO users (username, password, user_id) 					VALUES (?,?,?)');			$stmt_usr->execute([$newUsername,$newPassword,$lastID]);						echo "Account created successfully! You are now able to login.";		} else {			echo "A problem occured! Did you forget to fill in any fields? Please try again!";		}	} catch(PDOException $e) {		echo 'User could not be registered! <br>';    		echo $sql . '<br>' . $e->getMessage();  	}?>	</body></html>